create table "public"."uploaded_results" (
    "id" bigint generated by default as identity not null,
    "student_event_id" bigint not null,
    "report_link" text,
    "report_type" text,
    "visible" boolean not null default false,
    "disqualified" boolean not null default false
);


alter table "public"."events" add column "results_visible" boolean not null default false;

CREATE UNIQUE INDEX uploaded_results_pkey ON public.uploaded_results USING btree (id);

alter table "public"."uploaded_results" add constraint "uploaded_results_pkey" PRIMARY KEY using index "uploaded_results_pkey";

alter table "public"."uploaded_results" add constraint "uploaded_results_student_event_id_fkey" FOREIGN KEY (student_event_id) REFERENCES student_events(student_event_id) not valid;

alter table "public"."uploaded_results" validate constraint "uploaded_results_student_event_id_fkey";

